// src/components/Sidebar.jsx
import React, { useEffect, useState } from 'react';
import { Link, useLocation } from 'react-router-dom';

export default function Sidebar() {
  const location = useLocation();
  const [open, setOpen] = useState(true);

  // Load sidebar state from storage
  useEffect(() => {
    const saved = localStorage.getItem("sidebar_open");
    setOpen(saved !== "0");
  }, []);

  useEffect(() => {
    localStorage.setItem("sidebar_open", open ? "1" : "0");
  }, [open]);

  // Active route highlight
  const isActive = (path) => location.pathname.startsWith(path);

  return (
    <aside className={`pf-sidebar ${open ? "open" : "closed"}`}>

      {/* top: hamburger */}
      <div className="pf-sidebar-top">
        <button 
          className="pf-hamburger"
          onClick={() => setOpen(o => !o)}
        >
          {open ? "‚úñ" : "‚ò∞"}
        </button>

        {open && (
          <div className="pf-sidebar-brand" onClick={() => window.location.href = "/"}>
            <img src="/logo.png" className="pf-sidebar-logo" alt="logo" />
            <div>
              <div className="pf-title">InsightFlow</div>
              <div className="pf-sub">Enterprise Analytics</div>
            </div>
          </div>
        )}
      </div>

      {/* navigation list */}
      <nav className="pf-sidebar-nav">
        <ul>
          <li className={isActive("/energy-analysis") ? "active" : ""}>
            <Link to="/energy-analysis">
              ‚ö° {open && "Energy Analysis"}
            </Link>
          </li>

          <li className={isActive("/stock-analysis") ? "active" : ""}>
            <Link to="/stock-analysis">
              üìà {open && "Stock Analysis"}
            </Link>
          </li>

          <li className={isActive("/attendance-analysis") ? "active" : ""}>
            <Link to="/attendance-analysis">
              üë• {open && "Attendance Analysis"}
            </Link>
          </li>
        </ul>
      </nav>

      {/* fixed bottom pro section */}
      <div className="pf-sidebar-footer">
        {open ? (
          <>
            <div className="pf-compact-cta">
              <div className="pf-cta-title">Pro features</div>
              <div className="pf-cta-text">Export scheduled reports & team sharing</div>
              <button className="pf-primary pf-block">Upgrade</button>
            </div>
            <small style={{ color: "#6b7280" }}>v1.0.0</small>
          </>
        ) : (
          <div className="pf-compact-icon">‚≠ê</div>
        )}
      </div>
    </aside>
  );
}
