// src/components/Home.jsx
import React, { useState } from 'react';
import { useNavigate } from 'react-router-dom';

export default function Home() {
  const navigate = useNavigate();
  const [selection, setSelection] = useState('');

  const ANALYSES = [
    { id: 'energy-analysis', label: 'Energy Data Analysis' },
    { id: 'stock-analysis', label: 'Stock Data Analysis (Coming Soon)' },
    { id: 'attendance-analysis', label: 'School Attendance Analysis (Coming Soon)' },
  ];

  function onSelect(e) {
    const val = e.target.value;
    setSelection(val);
    if (!val) return;
    navigate(val === 'energy-analysis' ? '/energy-analysis' : `/${val}`);
  }

  return (
    <main className="pf-page">
      <div className="pf-hero" style={{ alignItems: 'center' }}>
        <div className="pf-hero-left">
          <h1>InsightFlow — Professional Analytics</h1>
          <p className="pf-lead">
            Turn raw energy datasets into actionable reports. Upload CSV or Excel files,
            run built-in analyses and get stakeholder-ready reports quickly.
          </p>

          <div className="pf-hero-actions" style={{ marginTop: 18 }}>
            <select
              className="pf-select"
              value={selection}
              onChange={onSelect}
              aria-label="Choose analysis"
            >
              <option value="">— Select analysis —</option>
              {ANALYSES.map(a => (
                <option key={a.id} value={a.id}>{a.label}</option>
              ))}
            </select>

            <button
              className="pf-outlined"
              onClick={() => navigate('/energy-analysis')}
              style={{ marginLeft: 12 }}
            >
              Try sample data
            </button>
          </div>
        </div>

        <div className="pf-hero-right">
          <div className="pf-card pf-stats">
            <div className="pf-stat">Uploads <strong>1,245</strong></div>
            <div className="pf-stat">Analyses <strong>842</strong></div>
            <div className="pf-stat">Avg. run <strong>7s</strong></div>
          </div>
        </div>
      </div>

      <section className="pf-features" style={{ marginTop: 28 }}>
        <article className="pf-feature-card">
          <h3>Secure by design</h3>
          <img
            src="/images/security-illustration.png"
            alt="Secure by design"
            style={{ width: "100%", borderRadius: "10px", margin: "12px 0" }}
          />
          <p>Role-based access control, SSO-ready, and encrypted storage.</p>
        </article>

        <article className="pf-feature-card">
          <h3>Shareable reports</h3>
          <p>Export PDF or schedule automated email reports for stakeholders.</p>
        </article>

        <article className="pf-feature-card">
          <h3>AI-driven insights</h3>
          <p>Auto-detect anomalies and get suggested actions.</p>
        </article>
      </section>
    </main>
  );
}
