// src/components/Home.jsx
import React, { useEffect, useRef, useState } from 'react';
import { useNavigate } from 'react-router-dom';
import './Home.css';

function AutoImagePanel({ images = [], interval = 4500, ariaLabel = 'Hero images' }) {
  const [index, setIndex] = useState(0);
  const paused = useRef(false);
  const timerRef = useRef(null);
  const panelRef = useRef(null);

  useEffect(() => {
    if (!images.length) return;
    start();
    return stop;
    // eslint-disable-next-line react-hooks/exhaustive-deps
  }, [images, interval]);

  function start() {
    stop();
    timerRef.current = setInterval(() => {
      if (!paused.current) setIndex(i => (i + 1) % images.length);
    }, interval);
  }
  function stop() {
    if (timerRef.current) {
      clearInterval(timerRef.current);
      timerRef.current = null;
    }
  }

  // subtle pointer parallax
  useEffect(() => {
    const el = panelRef.current;
    if (!el) return;
    const handleMove = (e) => {
      const rect = el.getBoundingClientRect();
      const px = ((e.clientX - rect.left) / rect.width - 0.5) * 14; // -7 .. 7
      const py = ((e.clientY - rect.top) / rect.height - 0.5) * 8; // -4 .. 4
      el.style.setProperty('--px', `${px}px`);
      el.style.setProperty('--py', `${py}px`);
    };
    const handleLeave = () => {
      el.style.setProperty('--px', `0px`);
      el.style.setProperty('--py', `0px`);
    };
    el.addEventListener('mousemove', handleMove);
    el.addEventListener('mouseleave', handleLeave);
    return () => {
      el.removeEventListener('mousemove', handleMove);
      el.removeEventListener('mouseleave', handleLeave);
    };
  }, []);

  if (!images.length) return null;

  return (
    <div
      className="auto-image-panel"
      ref={panelRef}
      role="img"
      aria-label={ariaLabel}
      onMouseEnter={() => { paused.current = true; }}
      onMouseLeave={() => { paused.current = false; }}
    >
      <div
        className="auto-image-inner"
        style={{ transform: `translateX(-${index * 100}%)` }}
      >
        {images.map((src, i) => (
          <div key={i} className="auto-image-slide" aria-hidden={i !== index}>
            <img src={src} alt={`hero ${i + 1}`} className="auto-image-img" />
            {/* Optional caption area (hidden visually but available if needed) */}
            <div className="auto-image-caption" aria-hidden="true" />
          </div>
        ))}
      </div>

      <div className="auto-image-dots" aria-hidden="false">
        {images.map((_, i) => (
          <button
            key={i}
            className={`dot ${i === index ? 'active' : ''}`}
            onClick={() => setIndex(i)}
            aria-label={`Show image ${i + 1}`}
            type="button"
          />
        ))}
      </div>
    </div>
  );
}

export default function Home() {
  const navigate = useNavigate();

  const heroImages = [
    '/images/home_page_1.jpg',
    '/images/home_page_2.jpg',
    '/images/home_page_3.jpg'
  ];

  const handleGetStarted = () => navigate('/analysis-selection');

  return (
    <main className="pf-page home-page">
      <div className="pf-hero connected-hero">
        <div className="pf-hero-left">
          <div className="eyebrow">Introducing InsightFlow</div>

          <h1 className="pf-title-main">
            Turn your data into clarity and momentum
          </h1>

          <p className="pf-lead">
{`InsightFlow uncovers the stories inside your spreadsheets and turns them into actionable, stakeholder-ready intelligence.
Upload CSV or Excel files and let our automated pipelines detect trends, surface anomalies and prepare polished reports.
From energy and financial metrics to attendance and custom datasets, get consistent insights quickly—without manual heavy lifting.
Save time, make confident decisions, and deliver impact across teams.`}
          </p>

          <div className="pf-hero-actions">
            <button className="btn-get-started" onClick={handleGetStarted} aria-label="Get started">
              Get Started →
            </button>

            <a href="/#features" className="btn-ghost" aria-label="Learn more about features">
              Learn more
            </a>
          </div>
        </div>

        <div className="pf-hero-right">
          <AutoImagePanel images={heroImages} interval={4800} ariaLabel="InsightFlow hero visuals" />
        </div>
      </div>

      {/* Features cards - located bottom before footer */}
      <section id="features" className="pf-features pf-features-bottom">
        <article className="pf-feature-card">
          <div className="feature-image-wrapper">
            <img src="/images/secure_by_design.png" alt="Secure by design" className="feature-image" />
            <div className="feature-text">
              <h3>Secure by design</h3>
              <p>Role-based access control, SSO-ready, and encrypted storage for enterprise peace of mind.</p>
            </div>
          </div>
        </article>

        <article className="pf-feature-card">
          <div className="feature-image-wrapper">
            <img src="/images/shareable_reports.png" alt="Shareable reports" className="feature-image" />
            <div className="feature-text">
              <h3>Shareable reports</h3>
              <p>Export PDFs, schedule automated emails and share polished reports with stakeholders.</p>
            </div>
          </div>
        </article>

        <article className="pf-feature-card">
          <div className="feature-image-wrapper">
            <img src="/images/ai_driven_insights.png" alt="AI-driven insights" className="feature-image" />
            <div className="feature-text">
              <h3>AI-driven insights</h3>
              <p>Auto-detect anomalies, highlight root causes, and get suggested actions to improve outcomes.</p>
            </div>
          </div>
        </article>
      </section>

      {/* Simple Footer */}
      <footer className="site-footer">
        <div className="footer-inner">
          <div className="footer-left">
            <img src="/images/logo_sm.png" alt="InsightFlow" className="footer-logo" />
            <div className="footer-copy">© {new Date().getFullYear()} InsightFlow — All rights reserved</div>
          </div>

          <div className="footer-right">
            <a href="/contact" className="footer-link">Contact</a>
            <a href="/privacy" className="footer-link">Privacy</a>
            <a href="/terms" className="footer-link">Terms</a>
            <div className="footer-built">Built by Your Company</div>
          </div>
        </div>
      </footer>
    </main>
  );
}
