// src/components/AnalysisSelection.jsx
import React from "react";
import { Link } from "react-router-dom";
import "./Home.css"; // We will reuse the same image card animations

export default function AnalysisSelection() {
  const analyses = [
    {
      name: "Energy Analysis",
      path: "/energy-analysis",
      icon: "âš¡",
      description: "Analyze energy patterns, detect anomalies, and generate insights.",
      active: true,
      image: "/images/energy_analysis.jpg"
    },
    {
      name: "Stock Analysis",
      path: "/stock-analysis",
      icon: "ðŸ“ˆ",
      description: "Understand stock movement trends and volatility metrics.",
      active: false,
      image: "/images/stock_analysis.jpg"
    },
    {
      name: "Attendance Analysis",
      path: "/attendance-analysis",
      icon: "ðŸ‘¥",
      description: "Monitor student or employee attendance patterns.",
      active: false,
      image: "/images/attendance_analysis.jpg"
    },
    {
      name: "Financial Analysis",
      path: "/financial-analysis",
      icon: "ðŸ“Š",
      description: "Evaluate spending trends and optimize financial decisions.",
      active: false,
      image: "/images/financial_analysis.jpg"
    },
    {
      name: "Asset Analysis",
      path: "/asset-analysis",
      icon: "ðŸ”§",
      description: "Track asset performance and detect operational inefficiencies.",
      active: false,
      image: "/images/asset_analysis.jpg"
    },
    {
      name: "Sustainability",
      path: "/sustainability",
      icon: "ðŸŒ±",
      description: "Measure environmental metrics and carbon footprint trends.",
      active: false,
      image: "/images/sustainability.jpg"
    },
    {
      name: "Custom Analysis",
      path: "/custom-analysis",
      icon: "ðŸ§©",
      description: "Create your own analytics pipeline tailored to your dataset.",
      active: false,
      image: "/images/custom_analysis.jpg"
    }
  ];

  return (
    <main className="analysis-selection-page">
      <h1 className="home-title">Explore Analyses</h1>
      <p className="home-subtitle">
        Select an analysis type to begin. Upload your dataset and generate insights instantly.
      </p>

      <div className="analysis-grid">
        {analyses.map((item) => (
          <div
            key={item.name}
            className={`pf-feature-card ${!item.active ? "disabled" : ""}`}
          >
            <div className="feature-image-wrapper">
              <img
                src={item.image}
                alt={item.name}
                className="feature-image"
              />

              <div className="feature-text">
                <h3>{item.name}</h3>
                <p>{item.description}</p>
              </div>
            </div>

            {item.active ? (
              <Link to={item.path} className="analysis-button-new">
                Start â†’
              </Link>
            ) : (
              <div className="coming-soon-tag">Coming Soon</div>
            )}
          </div>
        ))}
      </div>
    </main>
  );
}
