// src/components/Sidebar.jsx
import React, { useEffect, useState } from "react";
import { Link, useLocation } from "react-router-dom";

export default function Sidebar() {
  const location = useLocation();
  const [open, setOpen] = useState(true);

  // Load sidebar state from localStorage
  useEffect(() => {
    const saved = localStorage.getItem("insightflow_sidebar_open");
    setOpen(saved !== "0");
  }, []);

  // Save state
  useEffect(() => {
    localStorage.setItem("insightflow_sidebar_open", open ? "1" : "0");
  }, [open]);

  // All your analysis items (vertical list)
  const items = [
    { to: "/energy-analysis", label: "Energy Analysis", icon: "‚ö°" },
    { to: "/asset-analysis", label: "Asset Analysis", icon: "üîß" },
    { to: "/financial-analysis", label: "Financial Analysis", icon: "üìä" },
    { to: "/stock-analysis", label: "Stock Analysis", icon: "üìà" },
    { to: "/attendance-analysis", label: "Attendance Analysis", icon: "üë•" },
    { to: "/sustainability", label: "Sustainability", icon: "üå±" },
    { to: "/custom-analysis", label: "Custom Analysis", icon: "üß©" },
    { to: "/trend-analysis", label: "Trend Analysis", icon: "üìà" },
    { to: "/anomaly-detection", label: "Anomaly Detection", icon: "‚ö†Ô∏è" }
  ];

  const isActive = (path) => location.pathname.startsWith(path);

  return (
    <aside className={`pf-sidebar sidebar ${open ? "open" : "closed"}`} aria-expanded={open}>

      {/* HEADER ROW (hamburger + logo) */}
      <div className="sidebar-top">
        <button
          className="sidebar-hamburger"
          onClick={() => setOpen((o) => !o)}
          aria-label="Toggle Sidebar"
        >
          <span className="hamburger-icon">{open ? "‚úñ" : "‚ò∞"}</span>
        </button>

        {open && (
          <div className="sidebar-brand" onClick={() => window.location.href = "/"}>
            <img src="/logo.png" alt="InsightFlow" className="pf-logo sidebar-logo" />
            <div className="sidebar-brand-text">
              <div className="pf-title">InsightFlow</div>
              <div className="pf-sub">Enterprise Analytics</div>
            </div>
          </div>
        )}
      </div>

      {/* VERTICAL MENU */}
      <nav className="sidebar-nav">
        <ul>
          {items.map((item) => (
            <li key={item.to} className={`sidebar-item ${isActive(item.to) ? "active" : ""}`}>
              <Link to={item.to} className="sidebar-link" title={item.label}>
                <span className="sidebar-icon">{item.icon}</span>
                {open && <span className="sidebar-label">{item.label}</span>}
              </Link>
            </li>
          ))}
        </ul>
      </nav>

      {/* PRO FEATURE FIXED AT BOTTOM */}
      <div className="sidebar-footer">
        {open ? (
          <>
            <div className="pf-compact-cta">
              <div className="pf-cta-title">Pro features</div>
              <div className="pf-cta-text">Export scheduled reports & team sharing</div>
              <button className="pf-primary pf-block">Upgrade</button>
            </div>
            <div style={{ fontSize: 12, color: "#6b7280" }}>v1.0.0</div>
          </>
        ) : (
          <div className="sidebar-compact-cta" title="Pro">
            ‚≠ê
          </div>
        )}
      </div>
    </aside>
  );
}
