import { useParams } from 'react-router-dom';
import { useState } from 'react';

export default function AnalysisPage() {
  const { analysisType } = useParams();
  const [columns, setColumns] = useState([]);

  const onFakeUpload = () => {
    setColumns(['Date', 'Metric', 'Region', 'Category']);
  };

  return (
    <div className="pf-page">
      <div className="pf-grid">
        <div className="pf-col-main">
          <div className="pf-card">
            <h2>{analysisType?.replace('-', ' ')}</h2>

            <p className="pf-muted">
              Upload a dataset to run this analysis.
            </p>

            <div className="pf-upload">
              <input type="file" />
              <button className="pf-primary" onClick={onFakeUpload}>Upload & analyze</button>
            </div>

            {columns.length > 0 && (
              <div className="pf-panel">
                <h4>Detected columns</h4>
                <div className="pf-tags">
                  {columns.map(c => <span key={c} className="pf-tag">{c}</span>)}
                </div>
              </div>
            )}

            <div className="pf-results">
              <div className="pf-placeholder">
                Charts and results will appear here
              </div>
            </div>
          </div>
        </div>

        <aside className="pf-col-side">
          <div className="pf-card">
            <h4>Summary</h4>
            <p className="pf-muted">Export, share, or schedule this report.</p>
            <button className="pf-outlined pf-block">Export PDF</button>
          </div>
        </aside>
      </div>
    </div>
  );
}
