// src/components/Sidebar.jsx
import React, { useEffect, useState } from 'react';
import { Link, useLocation } from 'react-router-dom';

/**
 * Sidebar with:
 * - Collapsible state (persisted)
 * - 'More' panel for overflow items
 * - Pro box fixed at bottom
 *
 * NOTE: If you generate the `items` list dynamically, the More logic will work automatically.
 */
export default function Sidebar() {
  const location = useLocation();
  const [collapsed, setCollapsed] = useState(false);
  const [moreOpen, setMoreOpen] = useState(false);

  // persist collapse state
  useEffect(() => {
    try {
      const val = localStorage.getItem('insightflow_sidebar_collapsed');
      setCollapsed(val === '1');
    } catch (e) {}
  }, []);

  useEffect(() => {
    try {
      localStorage.setItem('insightflow_sidebar_collapsed', collapsed ? '1' : '0');
    } catch (e) {}
  }, [collapsed]);

  // Example list of analyses ‚Äî extend as needed (dynamic generation also supported)
  const items = [
    { to: '/', label: 'Dashboard', icon: 'üè†' },
    { to: '/energy-analysis', label: 'Energy Analysis', icon: '‚ö°' },
    { to: '/asset-analysis', label: 'Asset Analysis', icon: 'üîß' },
    { to: '/financial-analysis', label: 'Financial Analysis', icon: 'üìä' },
    { to: '/stock-analysis', label: 'Stock Analysis', icon: 'üìà' },
    { to: '/attendance-analysis', label: 'Attendance Analysis', icon: 'üë•' },
    { to: '/sustainability', label: 'Sustainability', icon: 'üå±' },
    { to: '/custom-analysis', label: 'Custom Analysis', icon: 'üß©' },
    // add more items here ‚Äî the "More" panel will appear automatically
  ];

  // visible count before "More" appears
  const VISIBLE_LIMIT = 5;

  const topItems = items.slice(0, VISIBLE_LIMIT);
  const overflowItems = items.slice(VISIBLE_LIMIT);

  const toggle = () => {
    setCollapsed(c => !c);
    // if collapsing, also close More panel to avoid weird state
    if (!collapsed) setMoreOpen(false);
  };

  const isActive = (to) => {
    if (to === '/') return location.pathname === '/';
    return location.pathname.startsWith(to);
  };

  return (
    <aside className={`pf-sidebar sidebar ${collapsed ? 'collapsed' : 'open'}`} aria-expanded={!collapsed}>
      <div className="sidebar-top">
        <div className="sidebar-brand" title="InsightFlow">
          <img src="/logo.png" alt="InsightFlow" className="pf-logo sidebar-logo" />
          {!collapsed && (
            <div className="sidebar-brand-text">
              <div className="pf-title">InsightFlow</div>
              <div className="pf-sub">Enterprise Analytics</div>
            </div>
          )}
        </div>

        <button
          className="sidebar-toggle"
          onClick={toggle}
          title={collapsed ? 'Expand sidebar' : 'Collapse sidebar'}
          aria-pressed={collapsed}
        >
          {collapsed ? '‚û°' : '‚¨Ö'}
        </button>
      </div>

      {/* navigation area: scrollable if many items */}
      <nav className="sidebar-nav" aria-label="Main">
        <ul>
          {topItems.map(item => (
            <li key={item.to} className={`sidebar-item ${isActive(item.to) ? 'active' : ''}`}>
              <Link to={item.to} className="sidebar-link" title={item.label} onClick={() => setMoreOpen(false)}>
                <span className="sidebar-icon">{item.icon}</span>
                {!collapsed && <span className="sidebar-label">{item.label}</span>}
              </Link>
            </li>
          ))}

          {/* If overflow exists, show a More button */}
          {overflowItems.length > 0 && (
            <li className={`sidebar-item more-item ${moreOpen ? 'open' : ''}`}>
              <button
                className="sidebar-link sidebar-more-btn"
                onClick={() => setMoreOpen(o => !o)}
                aria-expanded={moreOpen}
                title={moreOpen ? 'Hide more' : 'Show more'}
              >
                <span className="sidebar-icon">‚ãØ</span>
                {!collapsed && <span className="sidebar-label">{moreOpen ? 'Less' : 'More'}</span>}
              </button>

              {/* Collapsible overflow panel */}
              {!collapsed && moreOpen && (
                <div className="sidebar-more-panel" role="menu" aria-label="More analyses">
                  <ul>
                    {overflowItems.map(it => (
                      <li key={it.to} className={`sidebar-item ${isActive(it.to) ? 'active' : ''}`}>
                        <Link to={it.to} className="sidebar-link" onClick={() => setMoreOpen(false)}>
                          <span className="sidebar-icon">{it.icon}</span>
                          <span className="sidebar-label">{it.label}</span>
                        </Link>
                      </li>
                    ))}
                  </ul>
                </div>
              )}
            </li>
          )}
        </ul>
      </nav>

      {/* pro box fixed near bottom by flexible layout */}
      <div className="sidebar-footer">
        {!collapsed ? (
          <>
            <div className="pf-compact-cta" style={{ marginBottom: 8 }}>
              <div className="pf-cta-title">Pro features</div>
              <div className="pf-cta-text">Export scheduled reports & team sharing</div>
              <button className="pf-primary pf-block" style={{ marginTop: 8 }}>Upgrade</button>
            </div>
            <div style={{ fontSize: 12, color: '#6b7280' }}>v1.0.0</div>
          </>
        ) : (
          <div className="sidebar-compact-cta" aria-hidden="true">‚≠ê</div>
        )}
      </div>
    </aside>
  );
}
