import { useState } from 'react';
import { useNavigate } from 'react-router-dom';

const ANALYSES = [
  { id: 'energy-analysis', label: 'Energy Data Analysis' },
  { id: 'stock-analysis', label: 'Stock Data Analysis' },
  { id: 'attendance-analysis', label: 'School Attendance Analysis' },
];

export default function Home() {
  const navigate = useNavigate();
  const [selection, setSelection] = useState('');

  const handleChange = (e) => {
    const value = e.target.value;
    setSelection(value);
    if (value) navigate(`/${value}`);
  };

  return (
    <div style={{ fontFamily: 'Inter, sans-serif', background: '#f8fafc', minHeight: '100vh' }}>

      {/* HEADER */}
      <header
        style={{
          display: 'flex',
          justifyContent: 'space-between',
          alignItems: 'center',
          padding: '1rem 2rem',
          background: 'linear-gradient(90deg, #2563eb, #3b82f6)',
          color: 'white',
          boxShadow: '0 4px 12px rgba(0,0,0,0.1)',
        }}
      >
        <img src="/logo.png" alt="Logo" style={{ height: 44 }} />
        <h1 style={{ margin: 0, fontSize: '1.9rem', fontWeight: 600 }}>
          InsightFlow
        </h1>
      </header>

      {/* MAIN CONTENT */}
      <div style={{ maxWidth: 900, margin: '3rem auto', textAlign: 'center' }}>

        {/* INTRO PARAGRAPH */}
        <p
          style={{
            fontSize: '1.15rem',
            color: '#475569',
            lineHeight: 1.6,
            maxWidth: 750,
            margin: '0 auto 2.5rem',
          }}
        >
          InsightFlow is a modern, intuitive analysis platform designed to help you
          upload datasets, explore patterns, and generate actionable insights effortlessly.
          Whether you're working with energy consumption, stock market behavior, or
          attendance trends, InsightFlow simplifies the entire workflow into a smooth,
          fast, and user-friendly experience.
        </p>

        {/* CARD */}
        <div
          style={{
            background: 'white',
            padding: '2rem',
            borderRadius: 16,
            boxShadow: '0 10px 28px rgba(0,0,0,0.08)',
            maxWidth: 450,
            margin: '0 auto',
          }}
        >
          <p style={{ fontSize: '1.25rem', marginBottom: '1rem', fontWeight: 600 }}>
            Select an analysis type
          </p>

          <select
            value={selection}
            onChange={handleChange}
            style={{
              width: '100%',
              padding: '0.8rem 1rem',
              fontSize: '1rem',
              borderRadius: 12,
              border: '1px solid #cbd5e1',
              background: '#f1f5f9',
              cursor: 'pointer',
              transition: '0.2s',
            }}
          >
            <option value="">-- Choose an analysis --</option>
            {ANALYSES.map((a) => (
              <option key={a.id} value={a.id}>
                {a.label}
              </option>
            ))}
          </select>
        </div>
      </div>
    </div>
  );
}
