// src/components/AnalysisSlider.jsx
import React, { useRef, useEffect, useState } from "react";
import { Link, useLocation } from "react-router-dom";
import "./AnalysisSlider.css";

export default function AnalysisSlider({ items = [], collapsed = false }) {
  const sliderRef = useRef(null);
  const location = useLocation();
  const [showLeft, setShowLeft] = useState(false);
  const [showRight, setShowRight] = useState(false);

  useEffect(() => {
    const el = sliderRef.current;
    if (!el) return;

    const checkButtons = () => {
      setShowLeft(el.scrollLeft > 8);
      setShowRight(el.scrollWidth - el.clientWidth - el.scrollLeft > 8);
    };

    checkButtons();
    el.addEventListener("scroll", checkButtons);
    window.addEventListener("resize", checkButtons);
    return () => {
      el.removeEventListener("scroll", checkButtons);
      window.removeEventListener("resize", checkButtons);
    };
  }, [items.length]);

  const scrollBy = (distance) => {
    sliderRef.current?.scrollBy({ left: distance, behavior: "smooth" });
  };

  const scrollLeft = () => scrollBy(-220);
  const scrollRight = () => scrollBy(220);

  const isActive = (path) => {
    if (path === "/") return location.pathname === "/";
    return location.pathname.startsWith(path);
  };

  return (
    <div className="afs-wrapper">
      {showLeft && (
        <button className="afs-btn afs-left" onClick={scrollLeft} aria-label="Scroll left">
          ◀
        </button>
      )}

      <div className="afs-container" ref={sliderRef} role="list">
        {items.map((item) => (
          <Link
            role="listitem"
            key={item.to}
            to={item.to}
            className={`afs-item ${isActive(item.to) ? "active" : ""}`}
            title={item.label}
          >
            <span className="afs-icon">{item.icon}</span>
            {!collapsed && <span className="afs-label">{item.label}</span>}
          </Link>
        ))}
      </div>

      {showRight && (
        <button className="afs-btn afs-right" onClick={scrollRight} aria-label="Scroll right">
          ▶
        </button>
      )}
    </div>
  );
}
