// src/components/Home.js
import { useState, useRef, useEffect } from 'react';
import { useNavigate } from 'react-router-dom';

const ANALYSES = [
  { id: 'energy-analysis', label: 'Energy Data Analysis' },
  { id: 'stock-analysis', label: 'Stock Data Analysis' },
  { id: 'attendance-analysis', label: 'School Attendance Analysis' },
];

export default function Home() {
  const navigate = useNavigate();
  const [open, setOpen] = useState(false);
  const ref = useRef();

  useEffect(() => {
    function onDocClick(e) {
      if (ref.current && !ref.current.contains(e.target)) setOpen(false);
    }
    document.addEventListener('mousedown', onDocClick);
    return () => document.removeEventListener('mousedown', onDocClick);
  }, []);

  const onSelect = (id) => {
    setOpen(false);
    navigate(`/${id}`);
  };

  return (
    <div style={{ textAlign: 'center', padding: '2rem' }}>
      <h1>InsightFlow</h1>
      <p>Choose an analysis from the dropdown</p>

      <div ref={ref} style={{ display: 'inline-block', textAlign: 'left' }}>
        <button
          onClick={() => setOpen(o => !o)}
          aria-haspopup="menu"
          aria-expanded={open}
          style={{
            padding: '0.6rem 1rem',
            borderRadius: 8,
            border: '1px solid #007bff',
            background: '#007bff',
            color: '#fff',
            cursor: 'pointer',
            fontSize: '1rem'
          }}
        >
          Select Analysis â–¾
        </button>

        {open && (
          <ul
            role="menu"
            style={{
              margin: 0,
              padding: '0.5rem',
              listStyle: 'none',
              background: '#fff',
              boxShadow: '0 6px 18px rgba(0,0,0,0.12)',
              borderRadius: 8,
              marginTop: 8,
              minWidth: 260
            }}
          >
            {ANALYSES.map(a => (
              <li key={a.id} role="menuitem" style={{ margin: 0 }}>
                <button
                  onClick={() => onSelect(a.id)}
                  style={{
                    display: 'block',
                    width: '100%',
                    padding: '0.6rem 0.8rem',
                    background: 'transparent',
                    border: 'none',
                    textAlign: 'left',
                    cursor: 'pointer',
                    fontSize: '1rem'
                  }}
                >
                  {a.label}
                </button>
              </li>
            ))}
          </ul>
        )}
      </div>
    </div>
  );
}
