// src/components/EnergyAnalysis.jsx
import React, { useEffect, useRef, useState } from 'react';
import { useLocation, useNavigate } from 'react-router-dom';
import { useAuth } from '../auth/AuthProvider';
import axios from 'axios';

export default function EnergyAnalysis() {
  const auth = useAuth();
  const navigate = useNavigate();
  const location = useLocation();

  const [file, setFile] = useState(null);
  const [loading, setLoading] = useState(false);
  const [reportReady, setReportReady] = useState(null); // { reportId, summary, reportHtml, columns, storageKey }
  const fileRef = useRef();

  const isLoggedIn = !!auth.user;

  // restore report if navigated back after login
  useEffect(() => {
    const pendingKey = location.state?.pendingReportKey;
    if (pendingKey) {
      const raw = sessionStorage.getItem(pendingKey);
      if (raw) {
        try {
          const report = JSON.parse(raw);
          setReportReady(report);
          navigate(location.pathname, { replace: true, state: {} });
        } catch (err) {
          console.error('failed to parse pending report', err);
        }
      }
    }
    // eslint-disable-next-line react-hooks/exhaustive-deps
  }, [location.state]);

  // Icon-based info blocks (Option D)
  function InfoBlocks() {
    return (
      <div className="ea-section" style={{ marginTop: 12 }}>
        <div className="ea-card">
          <div className="ea-icon">‚ö°</div>
          <div className="ea-content">
            <h3>What is Energy Analysis?</h3>
            <p>
              Energy Analysis evaluates how electricity is consumed across your operations.
              It reveals patterns, inefficiencies, and opportunities to reduce energy cost.
            </p>
          </div>
        </div>

        <div className="ea-card">
          <div className="ea-icon">üìà</div>
          <div className="ea-content">
            <h3>How Energy Analysis Helps Your Business Grow</h3>
            <p>
              Lower operational costs, improve equipment performance, and enable reliable decision making through data-driven insights.
            </p>
          </div>
        </div>

        <div className="ea-card">
          <div className="ea-icon">‚ùì</div>
          <div className="ea-content">
            <h3>Why Energy Analysis?</h3>
            <p>
              Rising energy prices, sustainability requirements and equipment issues make Energy Analysis essential for operational efficiency.
            </p>
          </div>
        </div>

        <div className="ea-card">
          <div className="ea-icon">üì§</div>
          <div className="ea-content">
            <h3>Share Reports with Stakeholders</h3>
            <p>
              Export PDF/Excel, generate secure shareable links, or schedule automated email reports for stakeholders.
            </p>
          </div>
        </div>

        <div className="ea-card">
          <div className="ea-icon">üìù</div>
          <div className="ea-content">
            <h3>How to Generate a Report</h3>
            <p>
              Upload CSV/XLSX, click Analyze ‚Äî system will detect patterns and produce a ready-to-share report.
            </p>
          </div>
        </div>

        <div className="ea-card">
          <div className="ea-icon">üìä</div>
          <div className="ea-content">
            <h3>Sample Data Example</h3>
            <p>
              Typical columns: Date & Time, Energy_Consumption (kWh), Equipment/Zone.
            </p>
            <div className="ea-sample-table">
              <strong>Example:</strong><br />
              2025-01-01 00:00 ‚Äî 23.4 kWh ‚Äî HVAC System<br />
              2025-01-01 01:00 ‚Äî 21.7 kWh ‚Äî HVAC System<br />
              2025-01-01 02:00 ‚Äî 18.2 kWh ‚Äî Lighting
            </div>
          </div>
        </div>

        <div className="ea-card">
          <div className="ea-icon">üìò</div>
          <div className="ea-content">
            <h3>Definition</h3>
            <p>
              Energy Analysis is a structured, data-driven evaluation of consumption patterns
              that helps organizations reduce cost, enhance equipment reliability, and improve sustainability.
            </p>
          </div>
        </div>
      </div>
    );
  }

  // upload and wait for report from backend
  async function uploadAndWait(fileToUpload) {
    setLoading(true);
    setReportReady(null);
    try {
      const fd = new FormData();
      fd.append('file', fileToUpload);
      fd.append('analysisType', 'energy-analysis');

      const headers = auth.token ? { Authorization: `Bearer ${auth.token}` } : {};
      const url = 'http://localhost:5000/api/upload-and-generate'; // change to real endpoint

      const res = await axios.post(url, fd, { headers, timeout: 120000 });
      const report = res.data;
      const key = `pending_report_${report.reportId || Date.now()}`;
      try { sessionStorage.setItem(key, JSON.stringify(report)); } catch (e) { /* ignore */ }
      setReportReady({ ...report, storageKey: key });
    } catch (err) {
      console.warn('Upload failed or backend unreachable. Using mock report.', err?.message || err);
      const mock = {
        reportId: 'mock-' + Date.now(),
        columns: ['Date', 'Value', 'Category'],
        summary: 'Mock report generated (backend unreachable).',
        reportHtml: `<div style="font-family:inherit"><h3>Mock report</h3><p>This is a demo report because the backend failed or is not reachable.</p></div>`
      };
      const key = `pending_report_${mock.reportId}`;
      try { sessionStorage.setItem(key, JSON.stringify(mock)); } catch (e) { /* ignore */ }
      setReportReady({ ...mock, storageKey: key });
    } finally {
      setLoading(false);
    }
  }

  const onAnalyze = async (e) => {
    e?.preventDefault?.();
    if (!file) {
      alert('Please choose a file first');
      return;
    }
    await uploadAndWait(file);
  };

  const onLoginToView = () => {
    if (!reportReady || !reportReady.storageKey) return;
    const from = { pathname: location.pathname };
    navigate('/login', { state: { from, pendingReportKey: reportReady.storageKey } });
  };

  return (
    <div className="pf-page">
      <div className="pf-card">
        <div style={{ display: 'flex', justifyContent: 'space-between', alignItems: 'flex-start', gap: 16 }}>
          <div style={{ flex: 1, minWidth: 0 }}>
            <h1 style={{ marginTop: 0 }}>Energy Analysis</h1>
            <p className="pf-muted">
              Upload your energy data to detect trends, flag anomalies and produce stakeholder-ready reports.
            </p>
          </div>
          <div style={{ width: 240 }}>
            <div className="pf-card" style={{ padding: 12 }}>
              <strong>Quick actions</strong>
              <div style={{ marginTop: 10 }}>
                <button className="pf-outlined" onClick={() => { fileRef.current?.click?.(); }}>Choose file</button>
                <button className="pf-primary" style={{ marginLeft: 8 }} onClick={onAnalyze} disabled={loading}>
                  {loading ? 'Generating...' : 'Upload & Analyze'}
                </button>
              </div>
            </div>
          </div>
        </div>

        <InfoBlocks />

        <div style={{ marginTop: 18 }}>
          <form onSubmit={onAnalyze}>
            <div style={{ display: 'flex', gap: 12, alignItems: 'center' }}>
              <input
                ref={fileRef}
                type="file"
                accept=".csv,.xlsx"
                onChange={(e) => setFile(e.target.files[0])}
              />
              <button className="pf-primary" type="submit" disabled={loading}>
                {loading ? 'Generating report...' : 'Upload & Analyze'}
              </button>
            </div>
          </form>

          {/* Report area */}
          <div style={{ marginTop: 18 }}>
            {!reportReady && !loading && (
              <div className="pf-muted">No report yet. Upload a file to generate an analysis report.</div>
            )}

            {loading && (
              <div style={{ marginTop: 12 }}>
                <div>Generating report ‚Äî please wait...</div>
                <div style={{ marginTop: 8 }} className="pf-placeholder">Processing...</div>
              </div>
            )}

            {reportReady && (
              <div style={{ position: 'relative', marginTop: 12 }}>
                <div style={{ display: 'flex', justifyContent: 'space-between', alignItems: 'center' }}>
                  <div>
                    <strong>Report ready</strong>
                    <div className="pf-muted" style={{ fontSize: 13 }}>{reportReady.summary}</div>
                  </div>

                  {!isLoggedIn && (
                    <div>
                      <button className="pf-outlined" onClick={onLoginToView}>
                        Sign in to view report
                      </button>
                    </div>
                  )}
                </div>

                <div className="report-wrapper" style={{ marginTop: 12 }}>
                  <div className="report-content" aria-hidden={!isLoggedIn}>
                    <div style={{ padding: 16, background: '#fff' }}>
                      {reportReady.reportHtml ? (
                        <div dangerouslySetInnerHTML={{ __html: reportReady.reportHtml }} />
                      ) : (
                        <div>
                          <h4>Detected columns</h4>
                          <div style={{ display: 'flex', gap: 8, flexWrap: 'wrap' }}>
                            {(reportReady.columns || []).map((c) => (
                              <span key={c} className="pf-tag">{c}</span>
                            ))}
                          </div>
                        </div>
                      )}
                    </div>
                  </div>

                  {!isLoggedIn && (
                    <div
                      className="report-overlay card-elevated"
                      role="dialog"
                      aria-modal="true"
                      aria-label="Sign in required to view report"
                      onClick={onLoginToView}
                    >
                      <div className="overlay-inner">
                        <div style={{ fontSize: 18, fontWeight: 700, marginBottom: 8 }}>
                          Please sign in to view the full report
                        </div>
                        <div style={{ color: '#475569', marginBottom: 12 }}>
                          We generated your report. Sign in to view detailed charts, tables and recommendations.
                        </div>
                        <div style={{ display: 'flex', gap: 8, justifyContent: 'center' }}>
                          <button
                            className="pf-primary"
                            onClick={(e) => { e.stopPropagation(); onLoginToView(); }}
                          >
                            Sign in to view
                          </button>
                          <button
                            className="pf-outlined"
                            onClick={(e) => {
                              e.stopPropagation();
                              if (reportReady.storageKey) sessionStorage.removeItem(reportReady.storageKey);
                              setReportReady(null);
                            }}
                          >
                            Dismiss
                          </button>
                        </div>
                      </div>
                    </div>
                  )}
                </div>
              </div>
            )}
          </div>
        </div>
      </div>
    </div>
  );
}
