import React, { useState, useEffect, useRef } from 'react';
import { BrowserRouter as Router, Routes, Route, useNavigate, useParams } from 'react-router-dom';

// ---------------------------------------------
// InsightFlow — Gradient + Vibrant design (Tailwind)
// Single-file React app component. Default export: App
// Drop into src/App.jsx (with Tailwind configured) or adapt classes to plain CSS.
// ---------------------------------------------

function Header() {
  return (
    <header className="w-full bg-gradient-to-r from-indigo-600 via-violet-500 to-pink-500 text-white shadow-md">
      <div className="max-w-6xl mx-auto px-6 py-4 flex items-center justify-between">
        <div className="flex items-center gap-3">
          <div className="h-10 w-10 rounded-lg bg-white/20 flex items-center justify-center ring-1 ring-white/30">
            {/* simple logo mark */}
            <svg width="20" height="20" viewBox="0 0 24 24" fill="none" xmlns="http://www.w3.org/2000/svg">
              <rect x="3" y="3" width="18" height="18" rx="4" stroke="white" strokeWidth="1.5" />
              <path d="M7 14L10.5 9.5L13 12.5L17 7" stroke="white" strokeWidth="1.5" strokeLinecap="round" strokeLinejoin="round" />
            </svg>
          </div>

          <div>
            <div className="text-sm font-medium opacity-90">InsightFlow</div>
            <div className="text-xs opacity-80">Data • Visualize • Automate</div>
          </div>
        </div>

        <nav className="text-sm opacity-95">
          <a className="px-4 py-2 hover:underline cursor-pointer">Docs</a>
          <a className="px-4 py-2 hover:underline cursor-pointer">Contact</a>
          <button className="ml-3 bg-white/20 backdrop-blur-sm px-3 py-1 rounded-md">Sign in</button>
        </nav>
      </div>
    </header>
  );
}

// Home page — beautiful hero + dropdown card
function Home() {
  const ANALYSES = [
    { id: 'energy-analysis', label: 'Energy Data Analysis' },
    { id: 'stock-analysis', label: 'Stock Data Analysis' },
    { id: 'attendance-analysis', label: 'School Attendance Analysis' },
    { id: 'custom', label: 'Custom CSV Insights' },
  ];

  const navigate = useNavigate();
  const [selection, setSelection] = useState('');

  const onChange = (e) => {
    const val = e.target.value;
    setSelection(val);
    if (val) navigate(`/${val}`);
  };

  return (
    <main className="max-w-6xl mx-auto px-6 py-12">
      <section className="grid grid-cols-1 md:grid-cols-2 gap-8 items-start">
        {/* Left: Intro card */}
        <div className="order-2 md:order-1">
          <div className="bg-white rounded-2xl p-8 shadow-xl">
            <h2 className="text-3xl font-semibold text-slate-800 mb-3">Welcome to InsightFlow</h2>
            <p className="text-slate-600 leading-relaxed mb-6">
              InsightFlow provides a vibrant, fast and delightful experience for turning raw
              datasets into actionable insights. Upload CSV or Excel files, pick an analysis
              type and get visual reports with recommendations — instantly.
            </p>

            <div className="mt-4">
              <label className="block text-sm font-medium text-slate-700 mb-2">Choose analysis</label>
              <select
                value={selection}
                onChange={onChange}
                className="w-full rounded-lg border border-slate-200 px-4 py-3 bg-gradient-to-b from-white to-slate-50 shadow-sm focus:outline-none focus:ring-2 focus:ring-indigo-300"
              >
                <option value="">— Select an analysis —</option>
                {ANALYSES.map((a) => (
                  <option key={a.id} value={a.id}>{a.label}</option>
                ))}
              </select>
            </div>

            <div className="mt-6 text-sm text-slate-500">
              <strong>Tip:</strong> Try the <em>Energy Data Analysis</em> for a quick demo dataset.
            </div>
          </div>
        </div>

        {/* Right: Visual card with gradient accent */}
        <div className="order-1 md:order-2">
          <div className="rounded-2xl p-6 bg-gradient-to-tr from-white/60 to-white/40 backdrop-blur-sm shadow-lg border border-white/30">
            <div className="flex items-center justify-between mb-4">
              <div>
                <h3 className="text-lg font-medium text-slate-800">Quick Overview</h3>
                <p className="text-sm text-slate-500">Vibrant charts, fast processing, and clear outputs.</p>
              </div>
              <div className="text-xs text-slate-500">v1.0</div>
            </div>

            <div className="h-52 rounded-lg bg-gradient-to-b from-indigo-100 to-pink-50 flex items-center justify-center text-indigo-700">
              {/* Chart placeholder */}
              <div className="text-center">
                <div className="text-2xl font-semibold">Beautiful Charts</div>
                <div className="text-sm mt-2">Interactive, responsive, and exportable</div>
              </div>
            </div>

            <div className="mt-4 grid grid-cols-3 gap-3">
              <StatCard title="Uploads" value="1.2k" />
              <StatCard title="Analyses" value="84" />
              <StatCard title="Avg. time" value="8s" />
            </div>
          </div>
        </div>
      </section>

      {/* Feature row */}
      <section className="mt-10 grid grid-cols-1 md:grid-cols-3 gap-6">
        <FeatureCard title="Fast Processing" desc="Upload CSV/XLSX and get results in seconds." />
        <FeatureCard title="Shareable Reports" desc="Export PDF or share a report link with stakeholders." />
        <FeatureCard title="AI Recommendations" desc="Auto-generated suggestions for improvements and anomalies." />
      </section>
    </main>
  );
}

function StatCard({ title, value }) {
  return (
    <div className="p-3 rounded-lg bg-white/70 border border-white/40 text-center">
      <div className="text-sm text-slate-600">{title}</div>
      <div className="text-xl font-semibold text-slate-800">{value}</div>
    </div>
  );
}

function FeatureCard({ title, desc }) {
  return (
    <div className="bg-white rounded-xl p-6 shadow-md">
      <h4 className="font-medium text-slate-800 mb-2">{title}</h4>
      <p className="text-sm text-slate-500">{desc}</p>
    </div>
  );
}

// Analysis page — vibrant layout with upload & results
function AnalysisPage() {
  const { analysisType } = useParams();
  const [file, setFile] = useState(null);
  const [columns, setColumns] = useState([]);
  const [loading, setLoading] = useState(false);
  const fileRef = useRef();

  useEffect(() => {
    // any fetch for analysis meta could go here
  }, [analysisType]);

  const onUpload = async (e) => {
    e.preventDefault();
    if (!file) return;
    setLoading(true);
    // mock behavior: parse header locally (client-side) for demo
    try {
      // for demo: pretend we parsed columns
      await new Promise((r) => setTimeout(r, 900));
      setColumns(['Date', 'Value', 'Category', 'Region']);
    } catch (err) {
      console.error(err);
    } finally {
      setLoading(false);
    }
  };

  return (
    <main className="max-w-6xl mx-auto px-6 py-12">
      <div className="bg-white rounded-2xl p-8 shadow-xl">
        <div className="flex items-center justify-between">
          <div>
            <h2 className="text-2xl font-semibold text-slate-800">{formatTitle(analysisType)}</h2>
            <p className="text-sm text-slate-500 mt-1">Upload dataset and run the selected analysis.</p>
          </div>
          <div className="text-sm text-slate-500">Analysis engine • fast</div>
        </div>

        <div className="mt-6 grid grid-cols-1 lg:grid-cols-3 gap-6">
          <div className="lg:col-span-2">
            <div className="rounded-lg border border-slate-100 p-6">
              <h3 className="font-medium mb-3">Upload dataset</h3>
              <form onSubmit={onUpload}>
                <div className="flex gap-3 items-center">
                  <input
                    ref={fileRef}
                    type="file"
                    accept=".csv,.xlsx"
                    onChange={(e) => setFile(e.target.files[0])}
                    className="text-sm"
                  />
                  <button
                    type="submit"
                    className="ml-auto bg-gradient-to-r from-indigo-600 to-pink-500 text-white px-4 py-2 rounded-lg shadow-sm"
                    disabled={loading}
                  >{loading ? 'Processing...' : 'Upload & Analyze'}</button>
                </div>
              </form>

              {columns.length > 0 && (
                <div className="mt-6">
                  <h4 className="text-sm font-medium mb-2">Detected Columns</h4>
                  <div className="flex gap-2 flex-wrap">
                    {columns.map((c) => (
                      <span key={c} className="px-3 py-1 rounded-full bg-indigo-50 text-indigo-700 text-sm">{c}</span>
                    ))}
                  </div>
                </div>
              )}
            </div>

            {/* Results / charts placeholder */}
            <div className="mt-6 rounded-lg p-6 bg-gradient-to-b from-white to-slate-50 border border-slate-100">
              <h3 className="font-medium mb-3">Analysis Results</h3>
              <div className="h-64 rounded-md bg-white/40 flex items-center justify-center text-slate-500">
                <div>
                  <div className="text-lg font-semibold">Interactive results will appear here</div>
                  <div className="text-sm mt-2">Charts, tables and recommendations</div>
                </div>
              </div>
            </div>
          </div>

          {/* Right column: summary card */}
          <aside className="space-y-4">
            <div className="p-4 rounded-xl bg-gradient-to-tr from-indigo-50 to-pink-50 border border-white/60">
              <div className="text-xs text-slate-500">Summary</div>
              <div className="mt-2 text-slate-800 font-semibold">Ready to analyze</div>
              <div className="mt-3 text-sm text-slate-500">Upload a dataset to see insights, anomaly detection, and suggested actions.</div>
            </div>

            <div className="p-4 rounded-xl bg-white shadow-sm">
              <div className="flex items-center justify-between">
                <div className="text-sm">Export</div>
                <div className="text-xs text-slate-500">PDF / CSV</div>
              </div>
              <div className="mt-3">
                <button className="w-full bg-white/80 border border-slate-200 py-2 rounded-lg">Export Report</button>
              </div>
            </div>

            <div className="p-4 rounded-xl bg-white shadow-sm">
              <div className="text-sm text-slate-600">Helpful Links</div>
              <ul className="mt-2 text-sm text-slate-500 list-disc pl-4 space-y-1">
                <li>Sample datasets</li>
                <li>How analysis works</li>
                <li>Contact support</li>
              </ul>
            </div>
          </aside>
        </div>
      </div>
    </main>
  );
}

function formatTitle(slug) {
  if (!slug) return 'Analysis';
  return slug.split('-').map(s => s[0].toUpperCase() + s.slice(1)).join(' ');
}

// Root App
export default function App() {
  return (
    <Router>
      <div className="min-h-screen bg-gradient-to-b from-slate-50 to-white">
        <Header />
        <Routes>
          <Route path="/" element={<Home />} />
          <Route path="/:analysisType" element={<AnalysisPage />} />
        </Routes>

        {/* Footer */}
        <footer className="mt-12 py-6">
          <div className="max-w-6xl mx-auto px-6 text-center text-sm text-slate-500">
            © {new Date().getFullYear()} InsightFlow — Built with care
          </div>
        </footer>
      </div>
    </Router>
  );
}
