// src/components/AnalysisPage.jsx
import React, { useEffect, useRef, useState } from 'react';
import { useParams, useLocation, useNavigate } from 'react-router-dom';
import { useAuth } from '../auth/AuthProvider';
import axios from 'axios';

export default function AnalysisPage() {
  const { analysisType } = useParams();
  const auth = useAuth();
  const navigate = useNavigate();
  const location = useLocation();

  const [file, setFile] = useState(null);
  const [loading, setLoading] = useState(false);

  // When reportReady is null => no report yet.
  // When reportReady is an object => report produced by backend.
  const [reportReady, setReportReady] = useState(null);

  // If previewOnly = true & user is NOT logged in, we show blurred preview + overlay asking to login.
  const isLoggedIn = !!auth.user;
  const fileRef = useRef();

  // If user returns after login and navigation included pendingReportKey, unblur and reveal the stored report
  useEffect(() => {
    const pendingKey = location.state?.pendingReportKey;
    if (pendingKey) {
      const raw = sessionStorage.getItem(pendingKey);
      if (raw) {
        try {
          const report = JSON.parse(raw);
          setReportReady(report);
          // clean navigation state so it won't try again on refresh
          navigate(location.pathname, { replace: true, state: {} });
        } catch (err) {
          console.error('failed to parse pending report', err);
        }
      }
    }
    // eslint-disable-next-line react-hooks/exhaustive-deps
  }, [location.state]);

  // Helper: send file to backend and wait for report
  async function uploadAndWait(fileToUpload) {
    setLoading(true);
    setReportReady(null);
    try {
      const fd = new FormData();
      fd.append('file', fileToUpload);
      fd.append('analysisType', analysisType);

      const headers = auth.token ? { Authorization: `Bearer ${auth.token}` } : {};

      // Replace this URL with your real backend endpoint that accepts file and returns a report object
      const url = 'http://localhost:5000/api/upload-and-generate';

      // We POST the file and wait for server response. If server does asynchronous processing,
      // the endpoint should wait or return a final report; if the backend is async, adapt accordingly.
      const res = await axios.post(url, fd, { headers, timeout: 120000 });

      // Expecting backend to return something like:
      // { reportId: 'abc123', columns: [...], summary: '...', reportHtml: '<div>...</div>' }
      const report = res.data;

      // Store the final report in sessionStorage under a key so we can resume viewing after login
      const key = `pending_report_${report.reportId || Date.now()}`;
      try {
        sessionStorage.setItem(key, JSON.stringify(report));
      } catch (err) {
        console.warn('sessionStorage set failed (report might be too large)', err);
      }

      // set local state with the report
      setReportReady({ ...report, storageKey: key });

      // If user not logged in: show preview + overlay asks to login
      // If user logged in: we'll render full report below
    } catch (err) {
      console.error('upload failed', err);
      // fallback: if backend unavailable, create a mock report for demo
      const mock = {
        reportId: 'mock-' + Date.now(),
        columns: ['Date', 'Value', 'Category'],
        summary: 'Mock report generated (backend error).',
        reportHtml: `<div><h3>Mock Report</h3><p>This is a simulated report because the backend upload failed or returned error.</p></div>`
      };
      const key = `pending_report_${mock.reportId}`;
      try { sessionStorage.setItem(key, JSON.stringify(mock)); } catch (e) { /* ignore */ }
      setReportReady({ ...mock, storageKey: key });
    } finally {
      setLoading(false);
    }
  }

  // Called when user clicks Upload & Analyze
  const onAnalyze = async (e) => {
    e?.preventDefault?.();
    if (!file) {
      alert('Please choose a file first');
      return;
    }
    await uploadAndWait(file);
  };

  // If user clicks Login to view, redirect to /login with pendingReportKey and from path
  const onRequestLoginToView = () => {
    if (!reportReady || !reportReady.storageKey) return;
    const from = { pathname: location.pathname };
    navigate('/login', { state: { from, pendingReportKey: reportReady.storageKey } });
  };

  return (
    <div className="pf-page">
      <div className="pf-card">
        <h2>{analysisType ? analysisType.replace('-', ' ') : 'Analysis'}</h2>

        <form onSubmit={onAnalyze}>
          <div style={{ display: 'flex', gap: 12, alignItems: 'center' }}>
            <input
              ref={fileRef}
              type="file"
              accept=".csv,.xlsx"
              onChange={(e) => setFile(e.target.files[0])}
            />
            <button className="pf-primary" type="submit" disabled={loading}>
              {loading ? 'Generating report...' : 'Upload & Analyze'}
            </button>
          </div>
        </form>

        {/* Report area */}
        <div style={{ marginTop: 18 }}>
          {!reportReady && !loading && (
            <div className="pf-muted">No report yet. Upload a file to generate an analysis report.</div>
          )}

          {loading && (
            <div style={{ marginTop: 12 }}>
              <div>Generating report — please wait...</div>
              <div style={{ marginTop: 8 }} className="pf-placeholder">Processing...</div>
            </div>
          )}

          {reportReady && (
            <div style={{ position: 'relative', marginTop: 12 }}>
              {/* preview/title */}
              <div style={{ display: 'flex', justifyContent: 'space-between', alignItems: 'center' }}>
                <div>
                  <strong>Report ready</strong>
                  <div className="pf-muted" style={{ fontSize: 13 }}>{reportReady.summary}</div>
                </div>

                {/* If logged in -> no prompt; else prompt to login to view */}
                {!isLoggedIn && (
                  <div>
                    <button className="pf-outlined" onClick={onRequestLoginToView}>
                      Login to view report
                    </button>
                  </div>
                )}
              </div>

              {/* Report content (HTML from backend). If not logged in, blur/overlay */}
              <div
                className={isLoggedIn ? '' : 'report-blur-wrapper'}
                style={{ marginTop: 12, borderRadius: 8, overflow: 'hidden', border: '1px solid #e6eef8' }}
              >
                {/* This container renders reportHtml if present; otherwise show columns summary */}
                <div style={{ padding: 16, background: '#fff' }}>
                  {reportReady.reportHtml ? (
                    // Safely render small snippets — backend should sanitize HTML before sending.
                    <div dangerouslySetInnerHTML={{ __html: reportReady.reportHtml }} />
                  ) : (
                    <div>
                      <h4>Detected columns</h4>
                      <div style={{ display: 'flex', gap: 8, flexWrap: 'wrap' }}>
                        {(reportReady.columns || []).map((c) => (
                          <span key={c} className="pf-tag">{c}</span>
                        ))}
                      </div>
                    </div>
                  )}
                </div>

                {/* If not logged in show overlay */}
                {!isLoggedIn && (
                  <div className="report-overlay" onClick={onRequestLoginToView}>
                    <div>
                      <div style={{ fontSize: 18, fontWeight: 600 }}>Sign in to view the full report</div>
                      <div style={{ marginTop: 8 }}>We generated your report — please sign in to continue and see detailed results.</div>
                      <div style={{ marginTop: 12 }}>
                        <button className="pf-primary" onClick={onRequestLoginToView}>Login to view</button>
                        <button className="pf-outlined" style={{ marginLeft: 8 }} onClick={() => {
                          // optional: allow user to discard stored report
                          if (reportReady.storageKey) sessionStorage.removeItem(reportReady.storageKey);
                          setReportReady(null);
                        }}>Discard</button>
                      </div>
                    </div>
                  </div>
                )}
              </div>
            </div>
          )}
        </div>
      </div>
    </div>
  );
}
