// src/components/Sidebar.jsx
import React, { useEffect, useState } from 'react';
import { Link, useLocation } from 'react-router-dom';
import AnalysisSlider from './AnalysisSlider';
import './AnalysisSlider.css'; // slider css (also used by sidebar layout)

/**
 * Sidebar (uses AnalysisSlider for main items)
 * - Collapsible (persisted)
 * - Pro box fixed at bottom
 * - No Contact/Settings links (as requested)
 */

export default function Sidebar() {
  const location = useLocation();
  const [collapsed, setCollapsed] = useState(false);

  useEffect(() => {
    try {
      const val = localStorage.getItem('insightflow_sidebar_collapsed');
      setCollapsed(val === '1');
    } catch (e) {
      setCollapsed(false);
    }
  }, []);

  useEffect(() => {
    try {
      localStorage.setItem('insightflow_sidebar_collapsed', collapsed ? '1' : '0');
    } catch (e) {}
  }, [collapsed]);

  // Example: many items (slider will handle horizontal scrolling)
  const items = [
    { to: '/', label: 'Dashboard', icon: 'üè†' },
    { to: '/energy-analysis', label: 'Energy Analysis', icon: '‚ö°' },
    { to: '/asset-analysis', label: 'Asset Analysis', icon: 'üîß' },
    { to: '/financial-analysis', label: 'Financial Analysis', icon: 'üìä' },
    { to: '/stock-analysis', label: 'Stock Analysis', icon: 'üìà' },
    { to: '/attendance-analysis', label: 'Attendance Analysis', icon: 'üë•' },
    { to: '/sustainability', label: 'Sustainability', icon: 'üå±' },
    { to: '/custom-analysis', label: 'Custom Analysis', icon: 'üß©' },
    { to: '/trend-analysis', label: 'Trend Analysis', icon: 'üìà' },
    { to: '/anomaly-detection', label: 'Anomaly Detection', icon: '‚ö†Ô∏è' },
  ];

  // active helper for non-slider fallback (if needed)
  const isActive = (to) => {
    if (to === '/') return location.pathname === '/';
    return location.pathname.startsWith(to);
  };

  return (
    <aside className={`pf-sidebar sidebar ${collapsed ? 'collapsed' : 'open'}`} aria-expanded={!collapsed}>
      <div className="sidebar-top">
        <div className="sidebar-brand" title="InsightFlow" onClick={() => window.location.href = '/'}>
          <img src="/logo.png" alt="InsightFlow" className="pf-logo sidebar-logo" />
          {!collapsed && (
            <div className="sidebar-brand-text">
              <div className="pf-title">InsightFlow</div>
              <div className="pf-sub">Enterprise Analytics</div>
            </div>
          )}
        </div>

        <button
          className="sidebar-toggle"
          onClick={() => setCollapsed(c => !c)}
          title={collapsed ? 'Expand sidebar' : 'Collapse sidebar'}
          aria-pressed={collapsed}
        >
          {collapsed ? '‚û°' : '‚¨Ö'}
        </button>
      </div>

      {/* slider as main navigation */}
      <div className="sidebar-slider-wrap" style={{ padding: '8px 12px' }}>
        <AnalysisSlider items={items} collapsed={collapsed} />
      </div>

      {/* Optionally, show a small vertical nav when expanded (for quick access) */}
      {!collapsed && (
        <nav className="sidebar-nav alt-list" aria-label="Quick links" style={{ padding: '8px 12px' }}>
          <ul style={{ listStyle: 'none', padding: 0, margin: 0 }}>
            {/* show first 4 as quick vertical links */}
            {items.slice(0, 4).map(it => (
              <li key={it.to} className={`sidebar-item ${isActive(it.to) ? 'active' : ''}`}>
                <Link to={it.to} className="sidebar-link" style={{ display: 'flex', gap: 10, alignItems: 'center', padding: '8px 10px', borderRadius: 8 }}>
                  <span className="sidebar-icon">{it.icon}</span>
                  <span className="sidebar-label">{it.label}</span>
                </Link>
              </li>
            ))}
          </ul>
        </nav>
      )}

      {/* pro box fixed near bottom */}
      <div className="sidebar-footer">
        {!collapsed ? (
          <>
            <div className="pf-compact-cta" style={{ marginBottom: 8 }}>
              <div className="pf-cta-title">Pro features</div>
              <div className="pf-cta-text">Export scheduled reports & team sharing</div>
              <button className="pf-primary pf-block" style={{ marginTop: 8 }}>Upgrade</button>
            </div>
            <div style={{ fontSize: 12, color: '#6b7280' }}>v1.0.0</div>
          </>
        ) : (
          <div className="sidebar-compact-cta" aria-hidden="true">‚≠ê</div>
        )}
      </div>
    </aside>
  );
}
