// src/components/Home.js
import { useState } from 'react';
import { useNavigate } from 'react-router-dom';

const ANALYSES = [
  { id: 'energy-analysis', label: 'Energy Data Analysis' },
  { id: 'stock-analysis', label: 'Stock Data Analysis' },
  { id: 'attendance-analysis', label: 'School Attendance Analysis' },
];

export default function Home() {
  const navigate = useNavigate();
  const [selection, setSelection] = useState('');

  const onChange = (e) => {
    const value = e.target.value;
    setSelection(value);
    if (value) navigate(`/${value}`);
  };

  return (
    <div>

      {/* ===== HEADER BAR ===== */}
      <header
        style={{
          display: 'flex',
          justifyContent: 'space-between',
          alignItems: 'center',
          padding: '1rem 2rem',
          borderBottom: '1px solid #e5e7eb',
          background: '#ffffff',
        }}
      >
        {/* Logo Left */}
        <div style={{ display: 'flex', alignItems: 'center' }}>
          <img
            src="/logo.png"
            alt="Logo"
            style={{ height: 40, marginRight: '0.5rem' }}
          />
        </div>

        {/* InsightFlow Right */}
        <h1 style={{ margin: 0, fontSize: '1.8rem', color: '#1a202c' }}>
          InsightFlow
        </h1>
      </header>

      {/* ===== MAIN CONTENT ===== */}
      <div style={{ textAlign: 'center', padding: '3rem' }}>

        {/* NEW INTRO SECTION */}
        <p style={{ maxWidth: 700, margin: '0 auto 2rem', fontSize: '1.1rem', color: '#4a5568' }}>
          InsightFlow is a lightweight, user-friendly analysis platform that helps you
          upload your data, explore trends, and generate meaningful insights instantly.
          Whether you're analyzing energy usage, stock performance, or attendance trends,
          InsightFlow provides a simple and intuitive workflow to get accurate results
          without any complex tools.
        </p>

        <p style={{ marginBottom: '1.2rem', fontSize: '1.2rem' }}>
          Select an analysis type to begin
        </p>

        <div style={{ display: 'inline-block', minWidth: 260 }}>
          <select
            value={selection}
            onChange={onChange}
            style={{
              width: '100%',
              padding: '0.7rem 0.9rem',
              fontSize: '1rem',
              borderRadius: 6,
              border: '1px solid #cbd5e0',
              cursor: 'pointer',
            }}
          >
            <option value="">-- Select an analysis --</option>
            {ANALYSES.map(a => (
              <option key={a.id} value={a.id}>{a.label}</option>
            ))}
          </select>
        </div>
      </div>
    </div>
  );
}
