// src/components/Sidebar.jsx
import React, { useEffect, useState } from 'react';
import { Link, useLocation } from 'react-router-dom';

/**
 * Collapsible Sidebar
 * - Persists collapsed state to localStorage ('insightflow_sidebar_collapsed')
 * - Shows icons in collapsed mode with accessible titles
 */
export default function Sidebar() {
  const location = useLocation();
  const [collapsed, setCollapsed] = useState(false);

  // Load persisted state
  useEffect(() => {
    try {
      const val = localStorage.getItem('insightflow_sidebar_collapsed');
      setCollapsed(val === '1');
    } catch (e) {
      // ignore
    }
  }, []);

  // Persist state
  useEffect(() => {
    try {
      localStorage.setItem('insightflow_sidebar_collapsed', collapsed ? '1' : '0');
    } catch (e) {}
  }, [collapsed]);

  const items = [
    { to: '/', label: 'Dashboard', icon: 'ðŸ ' },
    { to: '/energy-analysis', label: 'Energy Analysis', icon: 'âš¡' },
    { to: '/stock-analysis', label: 'Stock Analysis', icon: 'ðŸ“ˆ' },
    { to: '/attendance-analysis', label: 'Attendance Analysis', icon: 'ðŸ‘¥' },
    { to: '/contact', label: 'Contact', icon: 'âœ‰ï¸' },
    { to: '/settings', label: 'Settings', icon: 'âš™ï¸' },
  ];

  const toggle = () => setCollapsed(c => !c);

  // Helper for active link
  const isActive = (to) => {
    if (to === '/') return location.pathname === '/';
    return location.pathname.startsWith(to);
  };

  return (
    <aside className={`pf-sidebar sidebar ${collapsed ? 'collapsed' : 'open'}`} aria-expanded={!collapsed}>
      <div className="sidebar-top">
        <div className="sidebar-brand" title="InsightFlow">
          <img src="/logo.png" alt="InsightFlow" className="pf-logo sidebar-logo" />
          {!collapsed && <div className="sidebar-brand-text">
            <div className="pf-title">InsightFlow</div>
            <div className="pf-sub">Enterprise Analytics</div>
          </div>}
        </div>

        <button
          className="sidebar-toggle"
          onClick={toggle}
          aria-label={collapsed ? 'Expand sidebar' : 'Collapse sidebar'}
          title={collapsed ? 'Expand sidebar' : 'Collapse sidebar'}
        >
          {collapsed ? 'âž¡' : 'â¬…'}
        </button>
      </div>

      <nav className="sidebar-nav" role="navigation" aria-label="Main">
        <ul>
          {items.map(item => (
            <li key={item.to} className={`sidebar-item ${isActive(item.to) ? 'active' : ''}`}>
              <Link to={item.to} className="sidebar-link" title={item.label}>
                <span className="sidebar-icon" aria-hidden="true">{item.icon}</span>
                {!collapsed && <span className="sidebar-label">{item.label}</span>}
              </Link>
            </li>
          ))}
        </ul>
      </nav>

      <div className="sidebar-footer">
        {collapsed ? (
          <div className="sidebar-compact-cta" aria-hidden="true">Pro</div>
        ) : (
          <>
            <div className="pf-compact-cta" style={{ marginBottom: 8 }}>
              <div className="pf-cta-title">Pro features</div>
              <div className="pf-cta-text">Export scheduled reports & team sharing</div>
              <button className="pf-primary pf-block" style={{ marginTop: 8 }}>Upgrade</button>
            </div>
            <div style={{ fontSize: 12, color: '#6b7280' }}>v1.0.0</div>
          </>
        )}
      </div>
    </aside>
  );
}
