import { useState, useEffect } from 'react';
import axios from 'axios';

function AnalysisPage({ match }) {
  const { analysisType } = match.params;
  const [info, setInfo] = useState({});
  const [file, setFile] = useState(null);
  const [columns, setColumns] = useState([]);
  const [loading, setLoading] = useState(false);

  useEffect(() => {
    const mockInfo = {
      'energy-analysis': {
        description: 'Analyze energy consumption patterns.',
        types: [
          { name: 'Trend Analysis', definition: 'Identifying patterns over time.', picture: '/images/trend-chart.png' },
          { name: 'Source Breakdown', definition: 'Analyzing energy sources.', picture: '/images/source-breakdown.png' }
        ],
        sampleData: 'Download sample CSV: <a href="/sample-energy-data.csv" download>here</a>',
        requiredData: 'CSV with columns: Date, Energy_Consumption, Source'
      },
      // Add for other types
    };
    setInfo(mockInfo[analysisType] || {});
  }, [analysisType]);

  const handleUpload = async (e) => {
    e.preventDefault();
    if (!file) return;
    setLoading(true);
    const formData = new FormData();
    formData.append('file', file);
    try {
      const response = await axios.post('http://localhost:5000/upload', formData);
      setColumns(response.data.columns);
    } catch (error) {
      alert('Upload failed: ' + error.response?.data?.error || error.message);
    } finally {
      setLoading(false);
    }
  };

  return (
    <div style={{ maxWidth: '1200px', margin: '0 auto', padding: '2rem' }}>
      <h1>{analysisType.replace('-', ' ').toUpperCase()}</h1>
      <p>{info.description}</p>
      <h2>Analysis Types</h2>
      {info.types?.map(type => (
        <div key={type.name} style={{ marginBottom: '2rem' }}>
          <h3>{type.name}</h3>
          <p>{type.definition}</p>
          <img src={type.picture} alt={type.name} style={{ maxWidth: '100%' }} />
        </div>
      ))}
      <h2>Required Data</h2>
      <p>{info.requiredData}</p>
      <div dangerouslySetInnerHTML={{ __html: info.sampleData }} />
      <form onSubmit={handleUpload} style={{ marginTop: '2rem' }}>
        <input type="file" accept=".csv,.xlsx" onChange={(e) => setFile(e.target.files[0])} required />
        <button type="submit" disabled={loading}>{loading ? 'Uploading...' : 'Upload'}</button>
      </form>
      {columns.length > 0 && (
        <div style={{ marginTop: '2rem' }}>
          <h2>Uploaded File Columns</h2>
          <ul>{columns.map(col => <li key={col}>{col}</li>)}</ul>
        </div>
      )}
    </div>
  );
}

export default AnalysisPage;
