// src/components/Header.jsx
import React from 'react';
import { Link, useNavigate } from 'react-router-dom';
import { useAuth } from '../auth/AuthProvider';

export default function Header() {
  const auth = useAuth();
  const navigate = useNavigate();

  const onSignOut = () => {
    auth.signout();
    navigate('/');
  };

  return (
    <header className="pf-header">
      <div className="pf-container pf-header-inner">
        <div className="pf-brand">
          <img src="/efficioTech_logo.png" alt="InsightFlow" className="pf-logo" />
          <div className="pf-brand-text">
            <div className="pf-title">InsightFlow</div>
            <div className="pf-sub">Enterprise Analytics</div>
          </div>
        </div>

        <div className="pf-controls">
          <input className="pf-search" placeholder="Search analyses, datasets..." />

          {!auth.user ? (
            <>
              <Link to="/login" className="pf-ghost">Sign in</Link>
              <Link to="/signup" className="pf-primary">Sign up</Link>
            </>
          ) : (
            <div style={{ display: 'flex', alignItems: 'center', gap: 12 }}>
              <div style={{ textAlign: 'right' }}>
                <div style={{ fontSize: 14, fontWeight: 600 }}>{auth.user.name}</div>
                <div style={{ fontSize: 12, color: '#6b7280' }}>{auth.user.email}</div>
              </div>
              <button className="pf-ghost" onClick={onSignOut}>Sign out</button>
            </div>
          )}
        </div>
      </div>
    </header>
  );
}
