// src/components/Home.jsx
import React, { useEffect, useRef, useState } from 'react';
import { useNavigate } from 'react-router-dom';
import './Home.css';

function AutoImagePanel({ images = [], interval = 4000, ariaLabel = 'Hero images' }) {
  const [index, setIndex] = useState(0);
  const paused = useRef(false);
  const timerRef = useRef(null);
  const panelRef = useRef(null);

  useEffect(() => {
    if (!images || images.length === 0) return;
    start();
    return stop;
    // eslint-disable-next-line react-hooks/exhaustive-deps
  }, [images, interval]);

  function start() {
    stop();
    timerRef.current = setInterval(() => {
      if (!paused.current) {
        setIndex(i => (i + 1) % images.length);
      }
    }, interval);
  }

  function stop() {
    if (timerRef.current) {
      clearInterval(timerRef.current);
      timerRef.current = null;
    }
  }

  // parallax / subtle pointer move effect
  useEffect(() => {
    const el = panelRef.current;
    if (!el) return;
    const handleMove = (e) => {
      const rect = el.getBoundingClientRect();
      const px = ((e.clientX - rect.left) / rect.width - 0.5) * 16; // -8 .. 8
      const py = ((e.clientY - rect.top) / rect.height - 0.5) * 8;  // -4 .. 4
      el.style.setProperty('--px', `${px}px`);
      el.style.setProperty('--py', `${py}px`);
    };
    const handleLeave = () => {
      el.style.setProperty('--px', `0px`);
      el.style.setProperty('--py', `0px`);
    };
    el.addEventListener('mousemove', handleMove);
    el.addEventListener('mouseleave', handleLeave);
    return () => {
      el.removeEventListener('mousemove', handleMove);
      el.removeEventListener('mouseleave', handleLeave);
    };
  }, []);

  if (!images || images.length === 0) return null;

  return (
    <div
      className="auto-image-panel"
      ref={panelRef}
      role="img"
      aria-label={ariaLabel}
      onMouseEnter={() => { paused.current = true; }}
      onMouseLeave={() => { paused.current = false; }}
    >
      <div className="auto-image-inner" style={{ transform: `translateX(-${index * 100}%)` }}>
        {images.map((src, i) => (
          <div className="auto-image-slide" key={i}>
            <img src={src} alt={`illustration ${i + 1}`} className="auto-image-img" />
          </div>
        ))}
      </div>

      <div className="auto-image-dots" aria-hidden="true">
        {images.map((_, i) => (
          <button
            key={i}
            className={`dot ${i === index ? 'active' : ''}`}
            onClick={() => setIndex(i)}
            aria-label={`Show image ${i + 1}`}
            type="button"
          />
        ))}
      </div>
    </div>
  );
}

export default function Home() {
  const navigate = useNavigate();

  const heroImages = [
    '/images/home_page.png',
    '/images/home_page.png',
    '/images/home_page.png'
  ];

  const handleGetStarted = () => {
    navigate('/analysis-selection');
  };

  return (
    <main className="pf-page home-page">
      <div className="pf-hero connected-hero">
        <div className="pf-hero-left">
          <h1 className="pf-title-main">
            InsightFlow — Turn data into decisive action
          </h1>

          <p className="pf-lead">
{`InsightFlow uncovers the signals hidden in ordinary datasets and converts them into clear,
decision-ready intelligence. Our automated pipelines ingest your CSV or Excel files, detect
important trends and anomalies, and produce polished, stakeholder-ready outputs — all with
minimal effort. Reduce analysis time from hours to minutes and empower teams to act with
confidence across operations, finance, sustainability and more.`}
          </p>

          <div className="pf-hero-actions">
            <button onClick={handleGetStarted} className="btn-get-started" aria-label="Get started">
              Get Started →
            </button>
          </div>
        </div>

        <div className="pf-hero-right">
          <AutoImagePanel images={heroImages} interval={5000} />
        </div>
      </div>

      {/* Features moved near footer */}
      <section className="pf-features pf-features-bottom">
        <article className="pf-feature-card">
          <div className="feature-image-wrapper">
            <img src="/images/secure_by_design.png" alt="Secure by design" className="feature-image" />
            <div className="feature-text">
              <h3>Secure by design</h3>
              <p>Role-based access control, SSO-ready, and encrypted storage.</p>
            </div>
          </div>
        </article>

        <article className="pf-feature-card">
          <div className="feature-image-wrapper">
            <img src="/images/shareable_reports.png" alt="Shareable reports" className="feature-image" />
            <div className="feature-text">
              <h3>Shareable reports</h3>
              <p>Export PDF or schedule automated email reports for stakeholders.</p>
            </div>
          </div>
        </article>

        <article className="pf-feature-card">
          <div className="feature-image-wrapper">
            <img src="/images/ai_driven_insights.png" alt="AI-driven insights" className="feature-image" />
            <div className="feature-text">
              <h3>AI-driven insights</h3>
              <p>Auto-detect anomalies and get suggested actions.</p>
            </div>
          </div>
        </article>
      </section>
    </main>
  );
}
